import React, { useState } from "react";
import "../App.css";

const API_URL = "http://127.0.0.1:5000/gerar";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [imageUrl, setImageUrl] = useState(null);
  const [lastNumber, setLastNumber] = useState(null);

  const handleSendMessage = async () => {
    if (input.trim() !== "" && !isNaN(input)) {
      const numero = parseInt(input, 10);

      setMessages([{ sender: "Usu치rio", text: input }]);
      setInput("");

      try {
        const response = await fetch(`${API_URL}/${numero}`);
        if (response.ok) {
          setImageUrl(`${API_URL}/${numero}`);
          setLastNumber(numero);

          setMessages([
            { sender: "Usu치rio", text: input },
            { sender: "Sistema" }
          ]);
        } else {
          setMessages([
            { sender: "Usu치rio", text: input },
            { sender: "Sistema", text: "Erro ao gerar a imagem." }
          ]);
        }
      } catch (error) {
        setMessages([
          { sender: "Usu치rio", text: input },
          { sender: "Sistema", text: "Falha ao conectar com a API." }
        ]);
      }
    } else {
      setMessages([{ sender: "Sistema", text: "Por favor, digite um n칰mero v치lido." }]);
    }
  };

  return (
    <div id="chat-container">
      <div id="chat-header">Chat dos N칰meros Cistercienses 游댝</div>
      <div id="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message-container ${msg.sender}`}>
            <div className="message-content">
              <i className={msg.sender === "Usu치rio" ? "fas fa-user" : "fas fa-robot"}></i>
              <b>{msg.sender}:</b> {msg.text}
              {msg.sender === "Sistema" && imageUrl && (
                <div className="image-container">
                  <img
                    src={imageUrl}
                    alt="N칰mero Cisterciense"
                    className="numero-imagem"
                  />
                  <div className="image-actions">
                    <button
                      className="image-button"
                      onClick={() => window.open(imageUrl, "_blank")}
                    >
                      游댌 Ampliar
                    </button>
                    <a
                      href={imageUrl}
                      download={`numero_cisterciense_${lastNumber}.png`}
                      className="image-button"
                    >
                      拘勇 Baixar
                    </a>
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="text"
          className="input-teclado"
          placeholder="Digite um n칰mero..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <button className="btn-enviar" onClick={handleSendMessage}>
          <i className="fas fa-paper-plane"></i> Enviar
        </button>
      </div>
    </div>
  );
};

export default Chat;
