import React, { useState, useEffect } from "react";
import "./App.css"; // Importa o CSS para estiliza칞칚o

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // Fun칞칚o para adicionar mensagens ao chat
  const addMessage = (text, sender) => {
    setMessages((prevMessages) => [...prevMessages, { text, sender }]);
  };

  // Fun칞칚o para enviar mensagem
  const sendMessage = async () => {
    if (!input.trim()) return;

    addMessage(input, "Usu치rio");
    setInput("");

    try {
      const response = await fetch("/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });

      const data = await response.json();
      addMessage(data.response, "Assistente");
    } catch (error) {
      addMessage("Erro ao conectar com o servidor.", "Assistente");
    }
  };

  // Carregar mensagem inicial ao iniciar o chat
  useEffect(() => {
    const loadInitialMessage = async () => {
      try {
        const response = await fetch("/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ message: "Iniciar" }),
        });

        const data = await response.json();
        addMessage(data.response, "Assistente");
      } catch (error) {
        addMessage("Erro ao conectar com o servidor.", "Assistente");
      }
    };

    loadInitialMessage();
  }, []);

  return (
    <div id="chat-container">
      <div id="chat-header">Chat dos N칰meros Cistercienses 游댝</div>
      <div id="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender}>
            <i className={msg.sender === "Usu치rio" ? "fas fa-user" : "fas fa-robot"}></i>
            <b>{msg.sender}:</b> {msg.text}
          </div>
        ))}
      </div>
      <div id="input-container">
        <input
          type="text"
          placeholder="Digite sua mensagem..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>
          <i className="fas fa-paper-plane"></i> Enviar
        </button>
      </div>
    </div>
  );
};

export default Chat;
