import React, { useState } from "react";

function Chat() {
  const [message, setMessage] = useState("");
  const [imageUrl, setImageUrl] = useState(null);

  const handleSendMessage = () => {
    // Aqui, futuramente, faremos a chamada da API
    console.log("Mensagem enviada:", message);
    setImageUrl("https://via.placeholder.com/300"); // Apenas um placeholder temporário
  };

  return (
    <div className="chat-container">
      <h2>Chat de Imagens</h2>
      <div className="chat-box">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Digite um número..."
        />
        <button onClick={handleSendMessage}>Enviar</button>
      </div>

      {imageUrl && (
        <div className="image-container">
          <img src={imageUrl} alt="Imagem carregada" width="300" />
          <button>Baixar Imagem</button>
        </div>
      )}
    </div>
  );
}

export default Chat;
