import React, { useState } from "react";
import { Send } from "lucide-react";

function Chat() {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  const sendMessage = () => {
    if (message.trim() !== "") {
      setMessages([...messages, message]);
      setMessage("");
    }
  };

  return (
    <div className="flex flex-col w-full max-w-md mx-auto p-4 border rounded-2xl shadow-lg bg-white">
      <div className="flex-1 overflow-y-auto p-2 space-y-2 border-b h-80">
        {messages.map((msg, index) => (
          <div key={index} className="bg-blue-500 text-white p-2 rounded-lg self-end">
            {msg}
          </div>
        ))}
      </div>
      <div className="flex items-center mt-2">
        <input
          type="text"
          className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Digite um número..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button
          onClick={sendMessage}
          className="ml-2 p-3 bg-blue-500 text-white rounded-full shadow-lg hover:bg-blue-600 transition"
        >
          <Send size={20} />
        </button>
      </div>
    </div>
  );
}

export default Chat;
