import React, { useState } from "react";
import "../App.css";

const API_URL = "http://127.0.0.1:5000/gerar"; // URL da API

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [imageUrl, setImageUrl] = useState(null); // Estado para armazenar a imagem gerada

  const handleSendMessage = async () => {
    if (input.trim() !== "" && !isNaN(input)) {
      const numero = parseInt(input, 10);
      
      setMessages([...messages, { sender: "Usu치rio", text: input }]); // Adiciona mensagem ao chat
      setInput("");

      try {
        const response = await fetch(`${API_URL}/${numero}`);
        if (response.ok) {
          setImageUrl(`${API_URL}/${numero}`); // Atualiza o estado com a imagem gerada
          // setMessages((prevMessages) => [
          //   ...prevMessages,
          //   { sender: "Sistema", text: "Imagem gerada com sucesso!" },
          // ]);
        } else {
          setMessages((prevMessages) => [
            ...prevMessages,
            { sender: "Sistema", text: "Erro ao gerar a imagem." },
          ]);
        }
      } catch (error) {
        setMessages((prevMessages) => [
          ...prevMessages,
          { sender: "Sistema", text: "Falha ao conectar com a API." },
        ]);
      }
    } else {
      setMessages((prevMessages) => [
        ...messages,
        { sender: "Sistema", text: "Por favor, digite um n칰mero v치lido." },
      ]);
    }
  };

  return (
    <div id="chat-container">
      <div id="chat-header">Chat dos N칰meros Cistercienses 游댝</div>
      <div id="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender}>
            <i className={msg.sender === "Usu치rio" ? "fas fa-user" : "fas fa-robot"}></i>
            <b>{msg.sender}:</b> {msg.text}
          </div>
        ))}
        {imageUrl && (
          <div className="image-container">
            <img src={imageUrl} alt="N칰mero Cisterciense" className="numero-imagem" />
          </div>
        )}
      </div>
      <div className="input-container">
        <input
          type="text"
          className="input-teclado"
          placeholder="Digite um n칰mero..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <button className="btn-enviar" onClick={handleSendMessage}>
          <i className="fas fa-paper-plane"></i> Enviar
        </button>
      </div>
    </div>
  );
};

export default Chat;
