import React, { useState } from "react";
import "../App.css";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const handleButtonClick = (letter) => {
    setInput(input + letter);
  };

  return (
    <div id="chat-container">
      <div id="chat-header">Chat dos NÃºmeros Cistercienses ðŸ”¢</div>
      <div id="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender}>
            <i className={msg.sender === "UsuÃ¡rio" ? "fas fa-user" : "fas fa-robot"}></i>
            <b>{msg.sender}:</b> {msg.text}
          </div>
        ))}
      </div>
      <div id="input-container">
        <input
          type="text"
          placeholder="Digite sua mensagem..."
          value={input}
          readOnly
        />
        <button>
          <i className="fas fa-paper-plane"></i> Enviar
        </button>
      </div>

      {/* Teclado Virtual */}
      <div className="teclado-container">
        <div className="teclado">
          {"QWERTYUIOP".split("").map((letter) => (
            <button key={letter} className="teclado-button" onClick={() => handleButtonClick(letter)}>
              {letter}
            </button>
          ))}
          {"ASDFGHJKL".split("").map((letter) => (
            <button key={letter} className="teclado-button" onClick={() => handleButtonClick(letter)}>
              {letter}
            </button>
          ))}
          {"ZXCVBNM".split("").map((letter) => (
            <button key={letter} className="teclado-button" onClick={() => handleButtonClick(letter)}>
              {letter}
            </button>
          ))}
          <button className="teclado-button teclado-space" onClick={() => handleButtonClick(" ")}>
            EspaÃ§o
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
