import React, { useState, useEffect } from "react";
import "../App.css"; // Importa o CSS para estilizaÃ§Ã£o

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // FunÃ§Ã£o para adicionar mensagens ao chat
  const addMessage = (text, sender) => {
    setMessages((prevMessages) => [...prevMessages, { text, sender }]);
  };

  // FunÃ§Ã£o para enviar mensagem

  return (
    <div id="chat-container">
      <div id="chat-header">Chat dos NÃºmeros Cistercienses ðŸ”¢</div>
      <div id="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender}>
            <i className={msg.sender === "UsuÃ¡rio" ? "fas fa-user" : "fas fa-robot"}></i>
            <b>{msg.sender}:</b> {msg.text}
          </div>
        ))}
      </div>
      <div id="input-container">
        <input
          type="text"
          placeholder="Digite sua mensagem..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>
          <i className="fas fa-paper-plane"></i> Enviar
        </button>
      </div>
    </div>
  );
};

export default Chat;
