import React, { useState } from "react";
import "../App.css";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const handleButtonClick = (value) => {
    setInput((prev) => prev + value);
  };

  return (
    <div id="chat-container">
      <div id="chat-header">Chat dos NÃºmeros Cistercienses ðŸ”¢</div>
      <div id="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender}>
            <i className={msg.sender === "UsuÃ¡rio" ? "fas fa-user" : "fas fa-robot"}></i>
            <b>{msg.sender}:</b> {msg.text}
          </div>
        ))}
      </div>

      {/* Teclado numÃ©rico estilizado */}
      <div className="teclado-container">
        <div className="display">
          <input className="display-input" type="text" value={input} readOnly />
        </div>
        <div className="teclado">
          {["1", "2", "3", "4", "5", "6", "7", "8", "9", "0"].map((num) => (
            <button key={num} className="teclado-button" onClick={() => handleButtonClick(num)}>
              {num}
            </button>
          ))}
        </div>
      </div>

      <div id="input-container">
        <input type="text" placeholder="Digite sua mensagem..." value={input} />
        <button>
          <i className="fas fa-paper-plane"></i> Enviar
        </button>
      </div>
    </div>
  );
};

export default Chat;
