import React, { useState } from "react";

function Chat() {
  const [message, setMessage] = useState("");
  const [imageUrl, setImageUrl] = useState(null);

  const handleSendMessage = async () => {
    if (!message) return;

    try {
      const response = await fetch(`https://sua-api.com/images/${message}`);
      const data = await response.json();
      setImageUrl(data.imageUrl); // Ajuste conforme o formato da resposta da API
    } catch (error) {
      console.error("Erro ao buscar imagem:", error);
    }
  };

  return (
    <div className="chat-container">
      <h2>Chat de Imagens</h2>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Digite um número..."
      />
      <button onClick={handleSendMessage}>Enviar</button>

      {imageUrl && (
        <div>
          <img src={imageUrl} alt="Imagem carregada" width="300" />
          <a href={imageUrl} download>
            <button>Baixar Imagem</button>
          </a>
        </div>
      )}
    </div>
  );
}

export default Chat;
